    <script type="text/javascript">
        var steps = document.querySelectorAll('.step');
        steps.forEach(
            function(currentValue) {
                currentValue.onmouseover = function() { this.style.opacity = 1; this.style.filter = 'saturate(1) brightness(1)'; };
                currentValue.onmouseout = function() { this.style.opacity = ''; this.style.filter = ''; };
            }
        );
        
        var stepIcon = document.querySelectorAll('.step-icon');
        stepIcon.forEach(
            function(currentValue) {
                currentValue.onmouseover = function() { document.querySelector('#logo-color').style.fill = getComputedStyle(this).fill; };
                currentValue.onmouseout = function() { document.querySelector('#logo-color').style.fill = ''; }
            }
        );
    </script>
